select FF.fecha_actual, F.hora_actual, U.nombre_usuario, U.apellido_usuario, P.id_proveedor, P.tipo_documento, P.identificacion_pro, P.empresa_pro, F.tipo_comprobante, F.fecha_registro, F.fecha_emision, F.fecha_caducidad, F.num_serie, F.num_autorizacion, F.fecha_cancelacion, F.forma_pago, F.tarifa0, F.tarifa12, F.iva_compra, F.descuento_compra, F.total_compra from factura_compra F, proveedores P, usuario U where F.id_usuario = U.id_usuario and P.id_proveedor = F.id_proveedor and F.id_factura_compra='1'